						["custom"] = "function(as, e, ...)\n    if e == \"FRAME_UPDATE\" then\n        if not aura_env.last or aura_env.last < GetTime() - 0.1 then\n            aura_env.last = GetTime()\n            return aura_env.scanTargets(as)\n        end\n    elseif e == \"NAME_PLATE_UNIT_ADDED\" then\n        local unit = ...\n        return aura_env.updateTarget(as, unit)\n    elseif e == \"UNIT_HEALTH\" then\n        local unit = ...\n        return aura_env.updateTarget(as, unit)\n    elseif e == \"UPDATE_MOUSEOVER_UNIT\" then\n        return aura_env.updateTarget(as, \"mouseover\")\n    elseif e == \"COMBAT_LOG_EVENT_UNFILTERED\" then\n        local _, subEvent, _, sourceGUID, sourceName, _, _, destGUID, _, _, _, spellID = ...\n        if subEvent == \"UNIT_DIED\" then\n            if destGUID and as[destGUID] then\n                as[destGUID].show = false\n                as[destGUID].changed = true\n                return true\n            end\n        elseif subEvent == \"SPELL_CAST_START\" then\n            if spellID ~= 64383 then return end -- Self Repair\n            \n            aura_env.startRepairState(as, sourceGUID, sourceName)\n            return true\n        elseif subEvent == \"SPELL_CAST_SUCCESS\" then\n            if spellID ~= 64383 then return end -- Self Repair\n            \n            if not as[sourceGUID] then return end\n            \n            aura_env.endRepairState(as, sourceGUID, sourceName)\n            return true\n        end\n    elseif e == \"PLAYER_TARGET_CHANGED\" then\n        local guid = UnitGUID(\"target\")\n        if guid and as[guid] then\n            as[guid].targeted = true\n            as[guid].changed = true\n            return true\n        end\n    elseif e == \"ENCOUNTER_START\" or e == \"ENCOUNTER_END\" then\n        -- Hide all states\n        for _,state in pairs(as) do\n            state.show = false\n            state.changed = true\n        end\n        return true\n    elseif e == \"OPTIONS\" then\n        if WeakAuras.IsOptionsOpen() then\n            for npcId,targetConfig in pairs(aura_env.tblTargetConfig) do\n                for scanCnt = 1, (targetConfig.scanLimit or 1) do\n                    as[format(\"Example-%d-%d\",npcId,scanCnt)] = {\n                        show = true,\n                        changed = true,\n                        progressType = \"static\",\n                        value = 100,\n                        total = 100,\n                        icon = targetConfig.icon,\n                        name = targetConfig.name,\n                        index = targetConfig.index,\n                    }\n                end\n            end\n            return true\n        end\n    end\nend",
