						["custom"] = "function()\n    \n    aura_env.time = aura_env.time or 0\n    if GetTime() >= aura_env.time+0.3 then\n        aura_env.time = GetTime()\n        aura_env.yogg = {}\n        aura_env.marks = {}\n        aura_env.enemy = {}\n        for i=1, 8 do\n            aura_env.marks[i] = false\n        end\n        \n        for i = 1, 40 do\n            local enemy = \"nameplate\"..i\n            if GetRaidTargetIndex(enemy) then\n                aura_env.marks[GetRaidTargetIndex(enemy)] = true\n            elseif UnitName(enemy) == \"Yogg\" then\n                table.insert(aura_env.yogg, enemy)\n            else\n                for key, value in pairs (aura_env.enemies) do\n                    if UnitName(enemy) == key and UnitAffectingCombat(enemy) then\n                        if value > 0 then \n                            if WA_GetUnitBuff(enemy, value) then\n                                table.insert(aura_env.enemy, enemy)\n                            end\n                        else\n                            table.insert(aura_env.enemy, enemy)\n                        end\n                    end\n                end\n            end\n        end\n        for i=1, #aura_env.yogg do\n            for j=1, 8 do\n                if aura_env.marks[j] == false then\n                    SetRaidTarget(aura_env.yogg[i], j)\n                    aura_env.marks[j] = true\n                    break\n                end\n            end\n        end\n        for i=1, #aura_env.enemy do\n            for j=3, 8 do\n                if aura_env.marks[j] == false then\n                    SetRaidTarget(aura_env.enemy[i], j)\n                    aura_env.marks[j] = true\n                    break\n                end\n            end\n        end\n        aura_env.marks = {}\n        aura_env.yogg = {}\n        aura_env.enemy = {}\n    end\nend\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n",
