			["fontSize"] = 26,
			["source"] = "import",
			["displayText_format_p_time_mod_rate"] = true,
			["anchorFrameType"] = "SCREEN",
			["frameStrata"] = 1,
			["actions"] = {
				["start"] = {
				},
				["finish"] = {
					["hide_all_glows"] = true,
				},
				["init"] = {
					["custom"] = "aura_env.tblTargetConfig = {\n    [\"33271\"] = {index = 1,  name = \"将军\",            icon = 254090, scanLimit=1, inactivitySecondsThreshold = 5}, -- General Vezax - Boss\n    [\"33524\"] = {index = 2,  name = \"畸体\",           icon = 135862, scanLimit=1, inactivitySecondsThreshold = 5}, -- General Vezax - Saronite Animus\n}\n\n-- Determines if all units have been found based on config settings\naura_env.doneScanning = nil\naura_env.isDoneScanning = function()\n    for _,v in pairs(aura_env.tblTargetConfig) do\n        if (not v.scanCnt) or (v.scanCnt == 0) or (v.scanCnt ~= v.scanLimit) then return end\n    end\n    return true\nend\n\n-- Initial some variables for scan counts\nfor _,targetConfig in pairs(aura_env.tblTargetConfig) do\n    targetConfig.scanCnt = 0\nend\n\n-- Update allstates table for a specific unit\naura_env.updateTarget = function(as, unit)\n    if not unit then return end\n    \n    local guid = UnitGUID(unit)\n    if not guid then return end\n    \n    -- Don't run twice for the same unit (this applies only when called by scanTargets)\n    if aura_env.updatedGUIDs and aura_env.updatedGUIDs[guid] then return end\n    \n    local npcId = select(6, strsplit(\"-\", guid))\n    if not npcId then return end\n    \n    local targetConfig = aura_env.tblTargetConfig[npcId]\n    if not targetConfig then return end\n    \n    -- Keep track of how many times this npc has been scanned so far (used with scanLimit and doneScanning)\n    targetConfig.scanCnt = targetConfig.scanCnt + 1\n    \n    local value = UnitHealth(unit) / UnitHealthMax(unit) * 100\n    local raidMarkIndex = GetRaidTargetIndex(unit)\n    \n    local changed_anything\n    \n    -- Only create state if health > 0 because CLEU:UNIT_DIED happens before the final UNIT_HEALTH event\n    if (value > 0) and (not as[guid]) then\n        as[guid] = {\n            show = true,\n            changed = true,\n            progressType = \"static\",\n            value = 100,\n            total = 100,\n            icon = targetConfig.icon,\n            name = nil, -- See below\n            index = targetConfig.index,\n            guid = guid,\n            npcId = npcId,\n            inactivitySecondsThreshold = targetConfig.inactivitySecondsThreshold,\n            raidMarkIndex = raidMarkIndex,\n            raidMark = raidMarkIndex and \"{rt\" .. raidMarkIndex .. \"}\",\n            dead = false,\n            autoHide = nil,\n        }\n        changed_anything = true\n    end\n    \n    if not as[guid] then return end\n    \n    -- Resolve an issue with UnitName returning \"Unknown\"\n    if not as[guid].name then\n        local name = targetConfig.name or UnitName(unit)\n        if name and (name ~= UNKNOWNOBJECT) then\n            as[guid].name = name\n            as[guid].changed = true\n            changed_anything = true\n        end\n    end\n    \n    if value ~= as[guid].value then\n        as[guid].value = value\n        as[guid].changed = true\n        changed_anything = true\n    end\n    \n    if raidMarkIndex ~= as[guid].raidMarkIndex then\n        as[guid].raidMarkIndex = raidMarkIndex\n        as[guid].raidMark = raidMarkIndex and \"{rt\" .. raidMarkIndex .. \"}\"\n        as[guid].changed = true\n        changed_anything = true\n    end\n    \n    as[guid].tsUpdate = GetTime()\n    \n    aura_env.doneScanning = aura_env.isDoneScanning()\n    if aura_env.updatedGUIDs then aura_env.updatedGUIDs[guid] = true end\n    return changed_anything\nend\n\n-- Update allstates table for all units found\naura_env.updatedGUIDs = nil\naura_env.scanTargets = function(as)\n    \n    local changed_anything\n    \n    -- Reset variables\n    aura_env.doneScanning = nil\n    aura_env.updatedGUIDs = {}\n    for _,targetConfig in pairs(aura_env.tblTargetConfig) do\n        targetConfig.scanCnt = 0\n    end\n    \n    -- Scan mouseover and focus\n    changed_anything = aura_env.updateTarget(as, \"mouseover\") or changed_anything\n    changed_anything = aura_env.updateTarget(as, \"focus\") or changed_anything\n    \n    -- Scan Nameplates\n    if not aura_env.doneScanning then\n        for _,v in pairs(C_NamePlate.GetNamePlates()) do\n            local unit = v.namePlateUnitToken\n            changed_anything = aura_env.updateTarget(as, unit) or changed_anything\n            if aura_env.doneScanning then break end\n        end\n    end\n    \n    -- Scan Raid+RaidPet Targets\n    if not aura_env.doneScanning then\n        for unit in WA_IterateGroupMembers() do\n            \n            -- Raid target\n            changed_anything = aura_env.updateTarget(as, unit .. \"target\") or changed_anything\n            if aura_env.doneScanning then break end\n            \n            -- Raid pet target\n            changed_anything = aura_env.updateTarget(as, unit .. \"pettarget\") or changed_anything\n            if aura_env.doneScanning then break end\n        end\n    end\n    \n    -- Hide old states that weren't updated for a while\n    for guid,state in pairs(as) do\n        if not aura_env.updatedGUIDs[guid] then\n            if as[guid].inactivitySecondsThreshold and (GetTime() - as[guid].tsUpdate) > as[guid].inactivitySecondsThreshold then\n                as[guid].show = false\n                state.changed = true\n                changed_anything = true\n            end\n        end\n    end\n    aura_env.updatedGUIDs = nil\n    return changed_anything\n    \nend",
					["do_custom"] = true,
